sqlMap.update:
/* comLogDAO.updateLogUsrStatDaily */ MERGE INTO TB_LOG_USR_STAT_DATE AS T USING (SELECT YMD = #ymd#, SITE_CD = #siteCd#) AS S ON ( T.YMD = S.YMD AND T.SITE_CD = S.SITE_CD ) WHEN MATCHED THEN UPDATE SET T.PV_CNT = PV_CNT+1
, T.UV_CNT = UV_CNT+1
, T.MOD_IDX = #modIdx# , T.MOD_NAME = #modName# , T.MOD_IP = #modIp# , T.MOD_DATE = GETDATE() WHEN NOT MATCHED BY TARGET THEN INSERT ( YMD , SITE_CD , UV_CNT , PV_CNT , REG_IDX , REG_NAME , REG_IP , REG_DATE ) VALUES ( #ymd# , #siteCd#
, 1
, 0
, 1 , #regIdx# , #regName# , #regIp# , GETDATE() );
